<style>
    #dec_frame{
        display: flex;
        flex-direction: column;
        align-items: center;

        width: 50%;
        hieght: 100%;
        padding: 0 5%;

        /*     background: blue;  */
    }

    #dec_frame h1 {
        color: var(--click, #CAC0A8);
        text-align: center;
        font-size: 2.11vw;
        font-style: normal;
        font-weight: 600;
        line-height: normal;
    }
    #dec_frame img {
        display: unset;

        width: 20%;
        min-width: 100px;
        height: 100%;

        /* background: blue; */
    }

    .svg_container{
        height: 9.9vw;
        /* border: 1px red solid; */
    }

    .svg_container svg{
        height: 100%;
    }
</style>

